.navbar li:hover>a {
    background: #cc1616;
}

.navbar .dropdown ul {
    background: #fff;
    box-shadow: 0px 0px 30px rgba(127, 137, 161, 0.25);
}

.navbar .dropdown ul a {
    color: #191919;
}

.navbar .dropdown ul a:hover,
.navbar .dropdown ul .active:hover,
.navbar .dropdown ul li:hover>a {
    color: #fff;
}

/**
* Mobile Navigation 
*/
